const Footer = () => {
  const navigation = useNavigation();
  const [tip, setTip] = useState(null);
  const onTipClick = text => {
    setTip(
      <View style={styles.tip}>
        <Text style={styles.tipText}>{text}</Text>
      </View>,
    );
    setTimeout(() => setTip(null), 3000);
  };

  return (
    <View style={styles.mainBlock}>
      {tip}
      <TouchableOpacity
        onPress={() => navigation.navigate('NewPhotoPrev')}
        style={styles.icon}>
        <SvgXml xml={addNew}/>
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => navigation.navigate('ChangeBackPrev')}
        style={styles.icon}>
        <SvgXml xml={myImages} />
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => navigation.navigate('StylePhotoPrev')}
        style={styles.icon}>
        <SvgXml xml={camera} />
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => navigation.navigate('GalleryPrev')}
        style={styles.icon}>
        <SvgXml xml={film} />
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => navigation.navigate('Settings')}
        style={styles.icon}>
        <SvgXml xml={settings} />
      </TouchableOpacity>
    </View>
  );
};
export default Footer;

